--Summary: This table is used to store ISO country information. Primarily it is used to translate the ISO 2 standards within the research database to ISO 3 for the visualization.



DROP TABLE kill_floor.country_information;

CREATE TABLE kill_floor.country_information
(
	country_name CHARACTER VARYING(100) NULL,
	modified_info CHARACTER VARYING(100) NULL,
	linked_country CHARACTER VARYING(100) NULL,
	country_iso3 CHARACTER VARYING(3) NULL,
	country_iso2 CHARACTER VARYING(2) NULL,
	grc CHARACTER VARYING(3)  NOT NULL,
	iso_number INTEGER NULL,
	country_alternate_name CHARACTER VARYING(100) NULL,
	imperative CHARACTER VARYING(100) NULL
);

SELECT *
FROM kill_floor.country_information;






